mapper.select:
select
id, course_id, user_id, user_comment, createtime, updatetime, obj1, obj2
from yikaodb.course_comment


        and id = #{id}


        and course_id = #{courseId}


        and user_id = #{userId}


        and user_comment = #{userComment}


        and createtime = #{createtime}


        and updatetime = #{updatetime}


        and obj1 = #{obj1}


        and obj2 = #{obj2}
