mapper.select:
select
id, user_id, course_title, course_Introduction, teacher_name, course_image, course_type, createtime, updatetime, private, obj1, obj2
from yikaodb.course


        and id = #{id}


        and user_id = #{userId}


        and course_title = #{courseTitle}


        and course_Introduction = #{courseIntroduction}


        and teacher_name = #{teacherName}


        and course_image = #{courseImage}


        and course_type = #{courseType}


        and createtime = #{createtime}


        and updatetime = #{updatetime}


        and private = #{private}


        and obj1 = #{obj1}


        and obj2 = #{obj2}
