mapper.select:
select
id, user_id, course_id, question, answer, createtime, updatetime, obj1, obj2
from yikaodb.judgment


        and id = #{id}


        and user_id = #{userId}


        and course_id = #{courseId}


        and question = #{question}


        and answer = #{answer}


        and createtime = #{createtime}


        and updatetime = #{updatetime}


        and obj1 = #{obj1}


        and obj2 = #{obj2}
