mapper.select:
select
id, my_user_id, for_user_id, message, createtime, updatetime, courseid, obj1, obj2
from yikaodb.message


        and id = #{id}


        and my_user_id = #{myUserId}


        and for_user_id = #{forUserId}


        and message = #{message}


        and createtime = #{createtime}


        and updatetime = #{updatetime}


        and courseid = #{courseid}


        and obj1 = #{obj1}


        and obj2 = #{obj2}
