mapper.select:
select
id, user_id, course_id, question, option1, option2, option3, option4, option5, option6, answer, createtime, updatetime, obj1, obj2
from yikaodb.multiple_choice


        and id = #{id}


        and user_id = #{userId}


        and course_id = #{courseId}


        and question = #{question}


        and option1 = #{option1}


        and option2 = #{option2}


        and option3 = #{option3}


        and option4 = #{option4}


        and option5 = #{option5}


        and option6 = #{option6}


        and answer = #{answer}


        and createtime = #{createtime}


        and updatetime = #{updatetime}


        and obj1 = #{obj1}


        and obj2 = #{obj2}
