mapper.select:
select
id, question_type, question_id, user_id, user_comment, createtime, updatetime, obj1, obj2
from yikaodb.question_comment


        and id = #{id}


        and question_type = #{questionType}


        and question_id = #{questionId}


        and user_id = #{userId}


        and user_comment = #{userComment}


        and createtime = #{createtime}


        and updatetime = #{updatetime}


        and obj1 = #{obj1}


        and obj2 = #{obj2}
