mapper.select:
select
id, user_id, type_id, course_id, question_type, question_id, answer, createtime, updatetime, obj1, obj2
from yikaodb.wrong_question


        and id = #{id}


        and user_id = #{userId}


        and type_id = #{typeId}


        and course_id = #{courseId}


        and question_type = #{questionType}


        and question_id = #{questionId}


        and answer = #{answer}


        and createtime = #{createtime}


        and updatetime = #{updatetime}


        and obj1 = #{obj1}


        and obj2 = #{obj2}
